import React, { Component } from 'react';
import logo from './zba.svg';
import axios from 'axios';
import "./App.css"

class Home extends Component {
  constructor() {
    super()
    this.state = {
      recipe:'',
      ingredientType:'',
      malt:'',
      creator:''
    }
  }

  onSubmit = (e) => {
    e.preventDefault()
    const { recipe, ingredientType, malt, creator } = this.state
    console.log(recipe, ingredientType, malt, creator)
    axios.post('http://localhost:8080/Recipe', { "recipe":"dqs",
      "ingredientType":"qsd",
      "malt":"dqsd",
      "creator":"qsd" })
      .then((result) => {
        console.log("Succesfully posted")
        console.log( recipe, ingredientType, malt, creator )
    });
    this.setState({ recipe: '' })
    this.setState({ ingredientType: '' })
    this.setState({ malt: '' })
    this.setState({ creator: '' })
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value })
  }

  render() {
    const { recipe, ingredientType, malt, creator } = this.state
    return (
    	<div className="App-in Font">
	    	<header className="App-header">
          		<img src={logo} className="Zba-logo" alt="logo" />
	    	</header>
        <form className="Div" onSubmit={this.onSubmit}>
          <label>Recipe</label>
          <br/><br/>
          <input type="text" name="field" id="recipe" value={recipe} onChange={this.onChange}/>
          <br/><br/>
          <label>Type of beer</label>
          <br/><br/>
          <input type="text" name="field" id="ingredientType" value={ingredientType} onChange={this.onChange}/>
          <br/><br/>
          <label>Type of malt</label>
          <br/><br/>
          <input type="text" name="field" id="malt" value={malt} onChange={this.onChange}/>
          <br/><br/>
          <label>Creator name</label>
          <br/><br/>
          <input type="text" name="field" id="creator" value={creator} onChange={this.onChange}/>
          <br/><br/>
          <button>Add new recipe</button>
          <br/><br/>
        </form>
    	</div>
    )
  }
}

export default Home;
