import axios from 'axios';
import "./App.css"



class RecipeTable extends Component {
  state = {
    recipe: []
  }

  componentDidMount() {
    axios.get(`https://localhost:8080/Recipe`)
      .then(res => {
        const recipe = res.data;
        this.setState({ recipe });
