import React, { Component } from 'react';
import axios from 'axios';
import "./App.css"



class RecipeTable extends Component {
  state = {
    recipe: []
  }

  componentDidMount() {
    axios.get(`http://localhost:8080/Recipe`, {headers: {"Access-Control-Allow-Origin": "*"}})
      .then(res => {
        const recipe = res.data;
        this.setState({ recipe });
    })
  }

  componentDidUpdate(prevProps) {
    if (this.props.update !== prevProps.update) {
      axios.get(`http://localhost:8080/Recipe`, {headers: {"Access-Control-Allow-Origin": "*"}})
        .then(res => {
          const recipe = res.data;
          this.setState({ recipe });
      })
    }
  }

  Delete() {
    axios.post('http://localhost:8080/Recipe{'+this.props.name+'}')
      .then((result) => {
        console.log("Succesfully deleted")
        console.log( this.props.name )
    });
  }

  render() {
    return(
      <div className="column2">
        <div className="inside">
          <h1>List recipe</h1>
          <table className="table">
            <thead>
              <tr>
                <th>Recipe</th>
                <th>Type of beer</th>
                <th>Type of malt</th>
                <th>Creator</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {this.state.recipe.map((recipe) => (
                <tr className="line" key={recipe.name}>
                  <td>{recipe.name}</td>
                  <td>{recipe.ingredientType}</td>
                  <td>{recipe.malt}</td>
                  <td>{recipe.creator}</td>
                  <td>
                    <button className="button smallButton">Edit</button>
                    <button className="button smallButton" onClick={() => this.Delete(recipe.name)}>Delete</button>
                  </td>
                </tr>
            ))}
            </tbody>
          </table>
        </div>
      </div>
    )
  }
}

export default RecipeTable;
