import React, { Component } from 'react';
import axios from 'axios';
import "./App.css"



class RecipeTable extends Component {
  state = {
    recipe: []
  }

  componentDidUpdate(prevProps) {
    if (this.props.update !== prevProps.update) {
      axios.get(`http://localhost:8080/Recipe`, {headers: {"Access-Control-Allow-Origin": "*"}})
        .then(res => {
          const recipe = res.data;
          this.setState({ recipe });
      })
    }
  }

  render() {
    return(
      <div className="column2">
        <div className="inside">
          <h1>List recipe</h1>
          <ul>
            { this.state.recipe.map(recipe => <li key={recipe.name}>   </li>)}
          </ul>

          <table className="table">
                        <thead>
                          <tr>
                          <th>Name</th>
                            <th>Recipe</th>
                            <th>Type of beer</th>
                            <th>Type of malt</th>
                            <th>Creator</th>
                            <th>Action</th>
                          </tr>
                        </thead>
                        <tbody>
                          {this.state.recipe.map((recipe) => (
                            <tr className="line" key="name">
                              <td>{recipe.name}</td>,<br/>
                              <td>{recipe.ingredientType}</td>
                              <td>{recipe.malt}</td>
                              <td>{recipe.creator}</td>
                              <td>
                                <button>Edit</button>
                                <button>Delete</button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                    </table>
        </div>
      </div>
    )
  }
}

export default RecipeTable;
